def get_db_connection():
    """Створює та повертає об'єкт з'єднання з базою даних."""
    try:
        connection = mysql.connector.connect(
            host=DB_HOST,
            user=DB_USER,
            password=DB_PASSWORD,
            database=DB_NAME
        )
        return connection
    except mysql.connector.Error as err:
        print(f"Error connecting to database: {err}")
        return None


def create_tables_and_populate_movies():
    """Створює таблиці 'users' і 'movies', якщо вони не існують, та заповнює таблицю фільмів."""
    connection = get_db_connection()
    if connection is None:
        print("Failed to create tables, database connection failed.")
        return

    cursor = connection.cursor()

    try:
        # Створюємо таблицю користувачів
        cursor.execute("""
                       CREATE TABLE IF NOT EXISTS users
                       (
                           id
                           INT
                           AUTO_INCREMENT
                           PRIMARY
                           KEY,
                           username
                           VARCHAR
                       (
                           50
                       ) NOT NULL UNIQUE,
                           password_hash VARCHAR
                       (
                           255
                       ) NOT NULL
                           )
                       """)

        # Створюємо таблицю фільмів з новими полями
        cursor.execute("""
                       CREATE TABLE IF NOT EXISTS movies
                       (
                           id
                           INT
                           AUTO_INCREMENT
                           PRIMARY
                           KEY,
                           title
                           VARCHAR
                       (
                           255
                       ) NOT NULL,
                           year INT NOT NULL,
                           status VARCHAR
                       (
                           50
                       ) NOT NULL,
                           image_url TEXT,
                           description TEXT
                           )
                       """)
        connection.commit()

        # Заповнюємо таблицю фільмів, якщо вона порожня
        cursor.execute("SELECT COUNT(*) FROM movies")
        if cursor.fetchone()[0] == 0:
            print("Adding dummy movie data...")
            movies_to_insert = [(m['title'], m['year'], m['status'], m['image_url'], m['description']) for m in
                                MOVIES_DATA]
            cursor.executemany(
                "INSERT INTO movies (title, year, status, image_url, description) VALUES (%s, %s, %s, %s, %s)",
                movies_to_insert)
            connection.commit()

    except mysql.connector.Error as err:
        print(f"Error Creating tables: {err}")
    finally:
        cursor.close()
        connection.close()


def save_new_user(username, password_hash):
    """Зберігає нового користувача в базі даних."""
    connection = get_db_connection()
    if connection is None:
        return False

    cursor = connection.cursor()
    try:
        sql = "INSERT INTO users (username, password_hash) VALUES (%s, %s)"
        val = (username, password_hash)
        cursor.execute(sql, val)
        connection.commit()
        return True
    except mysql.connector.Error as err:
        print(f"Database error: {err}")
        return False
    finally:
        cursor.close()
        connection.close()